import React, { Component } from 'react';
import Projects from './Projects';
import profile from './assets/profile.png';
import SocialProfiles from './SocialProfiles';
import Title from './Title';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Image from 'react-bootstrap/Image'



class App extends Component {
    state = {displayBio: false};
    //callback method
    toggleDisplayBio = () =>{
        this.setState({displayBio: !this.state.displayBio});
    }
    render(){
       /* const bio = this.state.displayBio ? ( 
            <div>
            <p>Hola</p>
            <p>quiero avanzar rapido</p>
            <p>Gracias, la gerencia</p>
        </div>
        ): null; //expresion ternaria
        */
        return (
            <Container>
                <Row className="justify-content-md-center">
                    <Image src={profile} alt="profile" className='profileImage' roundedCircle/>
                </Row>
                <Row>
                    <Col md="12" align="center">
                        <h1>Hello!</h1>
                        <p>My name is Maria</p>
                        <p>I am always looking for working</p>
                        <Title />                                
                    {this.state.displayBio ? ( 
                    <Container>
                        <Row>
                            <Col md="2">
                            <p>Hola</p>
                            </Col>
                            <Col md="5">
                            <p>quiero avanzar rapido</p>
                            </Col>
                            <Col md="5">
                            <p>Gracias, la gerencia</p>
                            </Col>
                        </Row>
                        <Button  onClick={this.toggleDisplayBio}>Show Less</Button>
                    </Container>
                         ): (<div>
                             <Button variant="light"onClick={this.toggleDisplayBio}>Read more</Button>
                             </div>
                         )
                    }
                    </Col>
                </Row>
               <hr />
               <Projects />
               <hr />
               <SocialProfiles />
            </Container>
        )
    }
}

export default App; //Permite a otras clases importar a App sin necesidad de ponerlo en {}
